using Newtonsoft.Json;

namespace Timma.Print
{
    class Logo : Row
    {
        private static readonly int LOGO_WIDTH = 340;
        public override string Type { get; } = "bmp";
        
        public Logo()
        {
            Data = "";
        }

        [JsonProperty("x")]
        public int XCoordinate { get; set; } = (Document.MAX_BITMAP_WIDTH - LOGO_WIDTH) / 2;

        [JsonProperty("reverse")]
        public int InvertColors { get; set; } = 1;
    }
}
